import { supabase } from '../supabase/supabase'

const ogimage = {
  src: OGImage,
  width: 1400,
  height: 450,
}

const Login = ({ email }) => {
  const [session, setSession] = useState(null)
    useEffect(() => {
      supabase.auth.getSession().then(({ data: { session } }) => {
        setSession(session)
      })

      const {
        data: { subscription },
      } = supabase.auth.onAuthStateChange((_event, session) => {
        setSession(session)
      })

      return () => subscription.unsubscribe()
    }, [])

  const getURL = () => {
    let url =
      process?.env?.API_URL ?? // Set this to your site URL in production env.
      'http://localhost:3000/'
    // Make sure to include `https://` when not localhost.
    url = url.includes('http') ? url : `https://${url}`
    // Make sure to include a trailing `/`.
    url = url.charAt(url.length - 1) === '/' ? url : `${url}/`
    return url
  }

  async function signInWithGitHub() {
    const { data, error } = await supabase.auth.signInWithOAuth({
      provider: 'github',
      options: {
        redirectTo: getURL(),
      },
    })
  }

  async function signInWithGoogle() {
    const { data, error } = await supabase.auth.signInWithOAuth({
      provider: 'google',
      options: {
        redirectTo: getURL(),
        queryParams: {
          access_type: 'offline',
          prompt: 'consent',
        },
      },
    })
  }

  async function signInWithEmail() {
    const { data, error } = await supabase.auth.signInWithOtp({
      email: {email},
      options: {
        emailRedirectTo: getURL(),
      },
    })
  }

  async function signOut() {
    const { error } = await supabase.auth.signOut()
  }

  return (
    <Layout>
      <div className="mb-48 ml-10 mr-10">
        <div>
          <Center>Login PubliusLogic</Center>
        </div>
        <LeftText>The Queen</LeftText>
        <ColumnGridTwo>
          <div className="mt-4">
            <div className="mb-4">
              <StaticImage
                layout="fixed"
                className="h-5 w-5 self-center rounded-lg"
                src="../../static/images/angie/ps-i-love-you.jpg"
                width={325}
                height={573}
                quality={95}
                alt="PS I Love You"
                loading="lazy"
              />
            </div>
          </div>
          <div className="-mt-2 mb-24 ml-8 text-slate-200 lg:col-span-2 lg:mt-0">
            {!session ? (
              <Auth
                supabaseClient={supabase}
                view="magic_link"
                appearance={{ theme: ThemeSupa }}
                providers={['github', 'google']}
                theme="dark"
              />
            ) : (
              <>
                <ColumnGridTwo>
                  <Account key={session.user.id} session={session} />
                  <div
                    className="flex h-full w-full flex-col items-center justify-center p-4"
                    style={{ minWidth: 250, maxWidth: 600, margin: 'auto' }}
                  >
                    <TodoList session={session} />
                  </div>
                </ColumnGridTwo>
              </>
            )}
          </div>
        </ColumnGridTwo>
      </div>
    </Layout>
  )
}

